import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../../components/ProductCard/ProductCard';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchHomeData();
  }, []);

  // Function to standardize API product data
  const transformProductData = (apiProduct) => {
    return {
      _id: apiProduct.id,
      name: apiProduct.title,
      // API returns a single 'image' string; we wrap it in an array
      images: [apiProduct.image], 
      description: apiProduct.description,
      price: apiProduct.price,
      category: apiProduct.category,
      brand: 'Generic Brand', // Placeholder
      rating: apiProduct.rating ? apiProduct.rating.rate : 0,
      numReviews: apiProduct.rating ? apiProduct.rating.count : 0,
      featured: true, // Mark all fetched products as featured for this section
      // Removed: countInStock
    };
  };

  const transformCategoryData = (categoryName) => {
    // We need to map Fake Store API names to our desired display names and links
    // The Fake Store API returns: electronics, jewelery, men's clothing, women's clothing
    let displayName = categoryName;
    let link = `/products?category=${categoryName}`;

    switch (categoryName) {
      case "men's clothing":
        displayName = "Men's Fashion";
        link = '/men'; // Use your specific route
        break;
      case "women's clothing":
        displayName = "Women's Fashion";
        link = '/women'; // Use your specific route
        break;
      case "jewelery":
        displayName = "Jewelry & Gifts";
        link = '/kids'; // Using /kids route as per our previous context
        break;
      case "electronics":
        displayName = "Electronics & Gadgets";
        break;
      default:
        break;
    }

    // Note: The Fake Store API doesn't provide image or count per category easily,
    // so we will use placeholders for those fields.
    return {
      id: categoryName,
      name: displayName,
      image: `https://source.unsplash.com/random/500x300/?${categoryName}`, // Random image based on name
      count: '50+', // Placeholder count
      link: link
    };
  };

  const fetchHomeData = async () => {
    const PRODUCTS_URL = 'https://fakestoreapi.com/products';
    const CATEGORIES_URL = 'https://fakestoreapi.com/products/categories';

    try {
      // 1. Fetch all products (to get featured products)
      const productsResponse = await fetch(PRODUCTS_URL);
      if (!productsResponse.ok) throw new Error('Failed to fetch products.');
      const allProducts = await productsResponse.json();

      // Simulate 'featured' products by taking the first 4 and transforming them
      const featured = allProducts.slice(0, 4).map(transformProductData);
      setFeaturedProducts(featured);

      // 2. Fetch all categories
      const categoriesResponse = await fetch(CATEGORIES_URL);
      if (!categoriesResponse.ok) throw new Error('Failed to fetch categories.');
      const categoryNames = await categoriesResponse.json();

      // Transform category names into structured data
      const categoryList = categoryNames.map(transformCategoryData);
      setCategories(categoryList);

    } catch (err) {
      console.error('Error fetching home data:', err);
      setError('Failed to load website content. Please try refreshing.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={styles.loading}>Loading...</div>;
  }

  if (error) {
    return <div style={{ ...styles.loading, color: 'red' }}>{error}</div>;
  }

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Welcome to The Digital Bazar</h1>
          <p style={styles.heroSubtitle}>
            Discover amazing products at unbeatable prices. Shop the latest trends in fashion, and more.
          </p>
          <Link to="/products" style={styles.heroButton}>
            Shop Now
          </Link>
        </div>
        <div style={styles.heroImage}>
          <img 
            // Using a placeholder image source for the hero banner
            src="https://images.unsplash.com/photo-1542838132-841961e6af72?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" 
            alt="Shopping"
            style={styles.heroImg}
          />
        </div>
      </section>

      {/* Categories Section */}
      <section style={styles.section}>
        <h2 style={styles.sectionTitle}>Shop by Category</h2>
        <div style={styles.categoriesGrid}>
          {categories.map(category => (
            <Link key={category.id} to={category.link} style={styles.categoryCard}>
              <img 
                src={category.image} 
                alt={category.name}
                style={styles.categoryImage}
              />
              <div style={styles.categoryContent}>
                <h3 style={styles.categoryName}>{category.name}</h3>
