import React, { useState, useEffect } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';

const Kids = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); 

  useEffect(() => {
    fetchKidsProducts();
  }, []);

  // Function to standardize API data to match your expected product structure
  const transformProductData = (apiProduct) => {
    return {
      _id: apiProduct.id,
      name: apiProduct.title,
      // API returns a single 'image' string; we wrap it in an array to match your mock 'images' array.
      images: [apiProduct.image], 
      description: apiProduct.description,
      price: apiProduct.price,
      // We manually set the category and brand since the API data is for 'jewelery'
      category: 'Kids (Jewelery/Gifts)', 
      brand: 'Kids Store Brand', // Placeholder brand
      rating: apiProduct.rating ? apiProduct.rating.rate : 0,
      numReviews: apiProduct.rating ? apiProduct.rating.count : 0,
      // Removed: countInStock key
    };
  };

  const fetchKidsProducts = async () => {
    // Using 'jewelery' category from Fake Store API as a placeholder for Kids
    const API_URL = "https://fakestoreapi.com/products/category/jewelery";

    try {
      const response = await fetch(API_URL);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      // Transform the fetched API data before setting it to state
      const standardizedProducts = data.map(transformProductData);

      setProducts(standardizedProducts);

    } catch (err) {
      console.error("Error fetching kids' products:", err);
      setError("Failed to load kids' products. Please check the network.");
    } finally {
      setLoading(false);
    }
  };

  // --- Rendering Logic ---

  if (loading) {
    return <div style={styles.loading}>Loading kids collection...</div>;
  }

  if (error) {
    // Added style for error message (based on the Men.js solution)
    return <div style={{ ...styles.loading, color: 'red' }}>{error}</div>; 
  }

  if (products.length === 0) {
    return (
      <div style={styles.container}>
        <div style={styles.header}>
          <h1 style={styles.title}>Kids Collection</h1>
        </div>
        <p style={{ ...styles.subtitle, textAlign: 'center' }}>No products found in this category.</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Kids Collection</h1>
        <p style={styles.subtitle}>Find adorable and comfortable items for your little ones</p>
      </div>

      <div style={styles.productsGrid}>
        {products.map(product => (
          <ProductCard key={product._id} product={product} /> 
        ))}
      </div>
    </div>
  );
};

// --- Your Original Styles ---
const styles = {
  container: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '2rem'
  },
  header: {
    textAlign: 'center',
    marginBottom: '3rem'
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
    marginBottom: '0.5rem'
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto'
  },
  productsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '2rem'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem'
  }
  // Note: I used an inline style for the error to avoid creating a new 'error' style object
};

export default Kids;
