import React, { useState, useEffect } from 'react';
import ProductCard from '../../components/ProductCard/ProductCard';

const Men = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); 

  useEffect(() => {
    fetchMenProducts();
  }, []);

  // Function to standardize API data to match your expected product structure
  const transformProductData = (apiProduct) => {
    return {
      _id: apiProduct.id,
      name: apiProduct.title,
      // Fake Store API returns a single 'image' string; we wrap it in an array to match your mock 'images' array.
      images: [apiProduct.image], 
      description: apiProduct.description,
      price: apiProduct.price,
      category: apiProduct.category,
      brand: 'Generic', // Placeholder
      rating: apiProduct.rating ? apiProduct.rating.rate : 0,
      numReviews: apiProduct.rating ? apiProduct.rating.count : 0,
      // Removed: countInStock: 10, 
    };
  };

  const fetchMenProducts = async () => {
    const API_URL = "https://fakestoreapi.com/products/category/men's clothing";

    try {
      const response = await fetch(API_URL);

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      // Transform the fetched API data before setting it to state
      const standardizedProducts = data.map(transformProductData);

      setProducts(standardizedProducts);

    } catch (err) {
      console.error("Error fetching men's products:", err);
      setError("Failed to load men's products. Please check the network.");
    } finally {
      setLoading(false);
    }
  };

  // --- Rendering Logic ---

  if (loading) {
    return <div style={styles.loading}>Loading men's collection...</div>;
  }

  if (error) {
    return <div style={styles.error}>{error}</div>;
  }

  if (products.length === 0) {
    return (
      <div style={styles.container}>
        <div style={styles.header}>
          <h1 style={styles.title}>Men's Collection</h1>
        </div>
        <p style={{ ...styles.subtitle, textAlign: 'center' }}>No products found in this category.</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Men's Collection</h1>
        <p style={styles.subtitle}>Discover our latest men's fashion and accessories</p>
      </div>

      <div style={styles.productsGrid}>
        {products.map(product => (
          <ProductCard key={product._id} product={product} /> 
        ))}
      </div>
    </div>
  );
};

// --- Your Original Styles ---
const styles = {
  container: {
    maxWidth: '1400px',
    margin: '0 auto',
    padding: '2rem'
  },
  header: {
    textAlign: 'center',
    marginBottom: '3rem'
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
    marginBottom: '0.5rem'
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#666',
    maxWidth: '600px',
    margin: '0 auto'
  },
  productsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
    gap: '2rem'
  },
  loading: {
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem'
  },
  error: { 
    textAlign: 'center',
    padding: '4rem',
    fontSize: '1.2rem',
    color: 'red'
  }
};

export default Men;
